MATCH (d:Drug)-[:DrugHasIndication]->(dis:Disorder)
WHERE toLower(d.displayName) = toLower('Ropinirole')
RETURN dis.primaryDomainId AS disorderId, dis.displayName AS disorderName;

MATCH (d:Drug)-[:DrugHasSideEffect]->(se:SideEffect)-[:SideEffectSameAsPhenotype]->(ph:Phenotype)
WHERE toLower(d.displayName) = toLower('Donepezil')
RETURN d.primaryDomainId AS drugId, d.displayName AS drugName, ph.primaryDomainId AS phenotypeId, ph.displayName AS phenotypeName;

MATCH (dis:Disorder {primaryDomainId:'mondo.0016419'}),(g:Gene)-[r:GeneAssociatedWithDisorder]->(dis)
WHERE r.scoreOpenTargets IS NULL OR r.scoreOpenTargets > 0.3
RETURN g.primaryDomainId AS geneId, g.displayName AS geneName;

MATCH (v:GenomicVariant)-[:VariantAssociatedWithDisorder]->(d1:Disorder {primaryDomainId:'mondo.0005147'}), 
      (v)-[:VariantAssociatedWithDisorder]->(d2:Disorder {primaryDomainId:'mondo.0005148'})
RETURN v.primaryDomainId AS variantId, v.displayName AS variantName, v.variantType, v.chromosome, v.position;

MATCH (v:GenomicVariant {chromosome:'2', position:78901})-[:VariantAssociatedWithDisorder]->(dis:Disorder)
WHERE dis.primaryDomainId IN ['mondo.0005105','mondo.0005012','mondo.0002971','mondo.0005208','mondo.0006320']
RETURN v.referenceSequence AS referenceSequence, v.primaryDomainId AS variantId, v.displayName AS variantName, dis.displayName AS disorderName, dis.primaryDomainId AS disorderId;

MATCH (sig:Signature)
WHERE toLower(sig.displayName) = toLower('Immunoglobulin signature')
RETURN sig.primaryDomainId AS signatureId, sig.displayName AS signatureName, sig.dataSources AS databaseSource;

MATCH (s1:Signature)-[:ProteinHasSignature]->(p:Protein)-[:ProteinHasSignature]->(s2:Signature)
WHERE toLower(s1.displayName) = 'ig-like_dom' AND toLower(s2.displayName) = 'ig_like'
RETURN p.primaryDomainId AS proteinId, p.geneName AS proteinGeneName, s1.displayName AS signature1, s2.displayName AS signature2;
