MATCH (d:Drug)-[:DrugHasIndication]->(dis:Disorder)
WHERE toLower(d.displayName) = toLower('Ropinirole')
RETURN dis.primaryDomainId AS disorderId, dis.displayName AS disorderName;

MATCH (d:Drug)-[:DrugHasTarget]->(p:Protein)
WHERE toLower(d.displayName) = toLower('Pregabalin')
RETURN p.primaryDomainId AS targetId, p.displayName AS targetName;

MATCH (dis:Disorder)
WHERE toLower(dis.displayName) = toLower('cystic fibrosis')
RETURN dis.primaryDomainId AS disorderId, dis.displayName AS disorderName;

MATCH (v:GenomicVariant)-[:VariantAssociatedWithDisorder]->(dis:Disorder {primaryDomainId:'mondo.0004379'})
WHERE v.variantType = 'SNP' AND v.chromosome = 'X'
RETURN v.primaryDomainId AS variantId, v.displayName AS variantName, v.variantType, v.chromosome, dis.primaryDomainId AS disorderId, dis.displayName AS disorderName;

MATCH (v:GenomicVariant)-[:VariantAffectsGene]->(g:Gene {primaryDomainId:'entrez.672'})
WHERE v.variantType = 'SNV'
RETURN v.primaryDomainId AS variantId, v.displayName AS variantName;

MATCH (dis:Disorder)-[:DisorderHasPhenotype]->(ph:Phenotype)
WHERE dis.primaryDomainId IN ['mondo.0010026','mondo.0014551','mondo.0014686','mondo.0010025','mondo.0014403']
RETURN DISTINCT ph.primaryDomainId AS phenotypeId, ph.displayName AS phenotypeName;

MATCH (s:Signature)
WHERE toLower(s.displayName) = toLower('Immunoglobulin')
RETURN s.primaryDomainId AS signatureId, s.displayName AS signatureName, s.dataSources AS dataSources;

MATCH (p:Protein)
WHERE toLower(p.displayName) = toLower('MYC_HUMAN')
RETURN p.primaryDomainId AS proteinId, p.displayName AS proteinName, p.sequence AS aminoAcidSequence;

MATCH (p:Protein)-[:ProteinEncodedByGene]->(g:Gene)
WHERE toLower(p.displayName) = toLower('1433E_HUMAN')
RETURN g.primaryDomainId AS geneId, g.displayName AS geneName;

MATCH (p:Protein)-[:ProteinHasSignature]->(s:Signature)
WHERE toLower(p.displayName) = toLower('CUL3_HUMAN')
RETURN s.primaryDomainId AS signatureId, s.displayName AS signatureName;

MATCH (tri:Protein) WHERE toLower(tri.displayName) = toLower('TRI67_HUMAN')
MATCH (tri)-[:ProteinInteractsWithProtein]-(partner:Protein)
RETURN partner.primaryDomainId AS partnerId, partner.displayName AS partnerName;

MATCH (p:Protein)
WHERE toLower(p.displayName) = toLower('HSP7C_HUMAN')
RETURN p.primaryDomainId AS proteinId, p.displayName AS proteinName, p.synonyms AS synonyms;

MATCH (d:Drug)-[:DrugHasTarget]->(p:Protein)-[:ProteinExpressedInTissue]->(t:Tissue)
WHERE toLower(t.displayName) = 'liver'
RETURN DISTINCT p.primaryDomainId AS proteinId, p.displayName AS proteinName, d.primaryDomainId AS drugId, d.displayName AS drugName;

MATCH (g:Gene)-[r]->(t:Tissue)
WHERE toLower(g.displayName) = toLower('BRCA2')
RETURN t.primaryDomainId AS tissueId, t.displayName AS tissueName;

MATCH (g:Gene)
WHERE toLower(g.displayName) = toLower('APC')
RETURN g.primaryDomainId AS geneId, g.displayName AS geneName, g.geneType AS geneType, g.description AS description;
